/*Table structure for table `city` */

DROP TABLE IF EXISTS `city`;

CREATE TABLE `city` (
  `CId` int(11) NOT NULL AUTO_INCREMENT,
  `CName` varchar(20) NOT NULL,
  `CStatus` varchar(20) NOT NULL DEFAULT 'N',
  `CDes` varchar(1000) NOT NULL,
