  PRIMARY KEY (`CId`)
) ENGINE=InnoDB AUTO_INCREMENT=8 DEFAULT CHARSET=utf8;

/*Data for the table `city` */

insert  into `city`(`CId`,`CName`,`CStatus`,`CDes`,`CPic`,`CHitNumber`) values (1,'广州','Y','广州，简称穗，别称羊城、花城，是广东省省会、副省级市、国家中心城市、超大城市、国际大都市、国际商贸中心、国际综合交通枢纽、国家综合性门户城市，首批沿海开放城市，是南部战区司令部驻地 。广州地处广东省中南部，珠江三角洲北缘，濒临南海，邻近香港、澳门，是中国通往世界的南大门，是粤港澳大湾区、泛珠江三角洲经济区的核心城市以及一带一路的枢纽城市。','upload\\item\\city\\1\\2019010617500414453.jpeg;',27),(2,'深圳','Y','深圳，简称“深”，别称“鹏城”，是中国四大一线城市之一，广东省省辖市、计划单列市、副省级市、国家区域中心城市、超大城市，国务院定位的全国经济中心城市和国际化城市、国家创新型城市、国际科技产业创新中心、全球海洋中心城市、国际性综合交通枢纽，中国三大全国性金融中心之一。\n','upload\\item\\city\\2\\2019010617504791138.jpg;upload\\item\\city\\2\\2019010617504767667.jpg;',8),(3,'上海','Y','上海，简称“沪”或“申”，是中国共产党的诞生地，中华人民共和国直辖市、国家中心城市、超大城市，国际经济、金融、贸易、航运、科技创新中心和综合交通枢纽，首批沿海开放城市。上海地处长江入海口，是长江经济带的龙头城市、G60科创走廊核心城市。隔东中国海与日本九州岛相望，南濒杭州湾，北、西与江苏、浙江两省相接。','upload\\item\\city\\3\\2019010617532057876.jpg;upload\\item\\city\\3\\2019010617532021665.jpg;upload\\item\\city\\3\\2019010617532034104.jpg;upload\\item\\city\\3\\2019010617532028686.jpg;',6),(4,'北京','Y','北京，简称“京”，是中华人民共和国的首都、直辖市、国家中心城市、超大城市、国际大都市，全国政治中心、文化中心、国际交往中心、科技创新中心和综合交通枢纽，是中国共产党中央委员会、中华人民共和国中央人民政府、全国人民代表大会、中国人民政治协商会议全国委员会、中华人民共和国中央军事委员会所在地，也是中部战区司令部驻地。','upload\\item\\city\\4\\2019010617535852120.jpeg;upload\\item\\city\\4\\2019010617535877808.jpeg;upload\\item\\city\\4\\2019010617535849783.jpeg;upload\\item\\city\\4\\2019010617535820854.jpeg;upload\\item\\city\\4\\2019010617535866102.jpg;',6),(5,'青岛','Y','青岛，山东省地级市，简称“青”，国家计划单列市、特大城市、副省级市，山东省经济中心、国家沿海重要中心城市、滨海度假旅游城市、国际性港口城市、国家重要的现代海洋产业发展先行区、东北亚国际航运枢纽、海上体育运动基地，“一带一路”新亚欧大陆桥经济走廊主要节点城市和海上合作战略支点。','upload\\item\\city\\5\\2019010617542846244.jpg;upload\\item\\city\\5\\2019010617542897176.jpg;',4),(6,'香港','Y','香港，简称“港”（HK），全称为中华人民共和国香港特别行政区（HKSAR）。地处中国华南地区，珠江口以东，南海沿岸，北接广东省深圳市，西接珠江，与澳门特别行政区、珠海市以及中山市隔着珠江口相望。\n香港是一座高度繁荣的国际大都市，区域范围包括香港岛、九龙、新界和周围262个岛屿，管辖陆地总面积1106.34平方公里，海域面积1648.69平方公里。截至2017年末，总人口约740.98万人，是世界上人口密度最高的地区之一。','upload\\item\\city\\6\\2019010617552327955.jpg;upload\\item\\city\\6\\2019010617552317539.jpg;upload\\item\\city\\6\\2019010617552367759.jpg;',7);

